mapper.select:
select ai.id albumId, ai.album_title, ai.cover_url, ai.include_track_count, ai.is_finished, ai.status, max(if(stat.stat_type='0401', stat.stat_num, 0)) playStatNum, max(if(stat.stat_type='0402', stat.stat_num, 0)) subscribeStatNum, max(if(stat.stat_type='0403', stat.stat_num, 0)) buyStatNum, max(if(stat.stat_type='0404', stat.stat_num, 0)) commentStatNum from album_info ai left join album_stat stat on stat.album_id = ai.id
and ai.user_id = #{userId}
and ai.status = #{vo.status}
and ai.album_title like concat('%', #{vo.albumTitle} ,'%')
and ai.is_deleted = 0
group by ai.id order by ai.id desc
